mapper.update:
UPDATE tbl_news_title


        title = #{update.title},


        content = #{update.content},


        pic = #{update.pic},


        postTime = #{update.postTime},


        updateTime = #{update.updateTime},


        status = #{update.status},





        AND id = #{condition.id}


        AND title = #{condition.title}


        AND content = #{condition.content}


        AND pic = #{condition.pic}


        AND postTime = #{condition.postTime}


        AND updateTime = #{condition.updateTime}


        AND status = #{condition.status}

UPDATE tbl_news_title
title = #{title}, content = #{content}, pic = #{pic}, postTime = #{postTime}, updateTime = #{updateTime}, status = #{status},
WHERE id = #{id}

mapper.delete:
DELETE FROM tbl_news_title


        AND id = #{id}


        AND title = #{title}


        AND content = #{content}


        AND pic = #{pic}


        AND postTime = #{postTime}


        AND updateTime = #{updateTime}


        AND status = #{status}

