mapper.update:
UPDATE tbl_news


        title = #{update.title},


        content = #{update.content},


        createDate = #{update.createDate},


        creator = #{update.creator},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        status = #{update.status},


        newsType = #{update.newsType},


        pic = #{update.pic},


        postTime = #{update.postTime},


        updateTime = #{update.updateTime},





        AND id = #{condition.id}


        AND title = #{condition.title}


        AND content = #{condition.content}


        AND createDate = #{condition.createDate}


        AND creator = #{condition.creator}


        AND lastModifier = #{condition.lastModifier}


        AND lastModDate = #{condition.lastModDate}


        AND status = #{condition.status}


        AND newsType = #{condition.newsType}


        AND pic = #{condition.pic}


        AND postTime = #{condition.postTime}


        AND updateTime = #{condition.updateTime}

UPDATE tbl_news
title = #{title}, content = #{content}, createDate = #{createDate}, creator = #{creator}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, newsType = #{newsType}, pic = #{pic}, postTime = #{postTime}, updateTime = #{updateTime},
WHERE id = #{id}

mapper.delete:
DELETE FROM tbl_news


        AND id = #{id}


