        AND creator = #{creator}


        AND lastModifier = #{lastModifier}


        AND lastModDate = #{lastModDate}


        AND status = #{status}


        AND newsType = #{newsType}


        AND pic = #{pic}


        AND postTime = #{postTime}


        AND updateTime = #{updateTime}

