UPDATE tbl_order
orderId = #{orderId}, createDate = #{createDate}, mFrom = #{mfrom}, mTo = #{mto}, content = #{content}, price = #{price}, carrier = #{carrier}, linkPhone = #{linkPhone}, status = #{status},
WHERE id = #{id}

mapper.delete:
DELETE FROM tbl_order


        AND id = #{id}


